import { ContentChildren, Directive, ElementRef, HostListener, QueryList, Renderer2, SimpleChanges } from '@angular/core';

@Directive({
  selector: '[appDataMoney]'
})
export class DataMoneyDirective {

  constructor(private el: ElementRef, private renderer: Renderer2) {}

  @ContentChildren('options') options!: QueryList<ElementRef>;

  ngAfterContentInit() {
    this.renderer.addClass(
      this.options.toArray()[0].nativeElement,
      'activeOption'
    );
  }

  @HostListener('mouseover', ['$event.target'])
  onMouseover(target: any) {
    this.actionElement('onHover', target);
  }
  @HostListener('mouseleave') onMouseLeave() {
    console.log("leave")
    this.el.nativeElement.style.display = false
  }

}
