import { Directive, ElementRef, HostListener, SimpleChanges } from '@angular/core';

@Directive({
  selector: '[appDataMoney]'
})
export class DataMoneyDirective {

  constructor(private el: ElementRef) {}

  @HostListener('mouseEnter') onMouseEnter() {
    this.el.nativeElement.style.display = true
  }
  @HostListener('mouseLeave') onMouseLeave() {
    this.el.nativeElement.style.display = false
  }

}
