import {
  Directive,
  ElementRef,
  OnInit,
  Renderer2,
  Input,
  OnChanges,
  SimpleChanges,
} from '@angular/core';
import { ChartService } from '../services/chart.service';

@Directive({
  selector: '[appDataMoney]',
})
export class DataMoneyDirective implements OnInit, OnChanges {
  @Input() perce!: any;
  constructor(private chartService: ChartService, private renderer: Renderer2) {}

  ngOnInit(): void {
    console.dir(this.perce);
  this.chartService.percentages$.subscribe(data => {
    if(this.perce === data) {
      this.renderer.set
    }
  })

  }
  ngOnChanges(changes: SimpleChanges): void {
    console.log(changes);
  }
}
