import { Directive, ElementRef, Input,OnInit } from '@angular/core';

@Directive({
  selector: '[percent]'
})
export class DataChartDirective implements OnInit{
  @Input() percentage!: string ; 

  constructor(private el: ElementRef) {}
  ngOnInit(): void {
    console.log(this.percentage)
    this.el.nativeElement.style.minHeight = (this.percent * 200) + 'px'
    this.el.nativeElement.style.backgroundColor = this.percentage
  }



}
