@Directive({
  selector: '[percent]'
})
export class DataChartDirective {
  @Input() percentage =""; 

  constructor(private el: ElementRef) { 
    console.log(this.percentage)
    // this.el.nativeElement.style.minHeight = (this.percent * 200) + 'px'
    this.el.nativeElement.style.backgroundColor = this.percent

  }



}
