import {
  Directive,
  ElementRef,
  OnInit,
  Renderer2,
  Input,
  OnChanges,
  SimpleChanges,
} from '@angular/core';
import { ChartService } from '../services/chart.service';

@Directive({
  selector: '[appDataMoney]',
})
export class DataMoneyDirective {
  @Input() perce!: any;
  constructor(
    private chartService: ChartService,
    private renderer: Renderer2,
    private el: ElementRef
  ) {}

  ngOnInit(): void {
    setTimeout(() => {
      this.chartService.percentages$.subscribe((data) => {
        console.log(data)
        if (this.perce === data) {
          this.renderer.setStyle(this.el.nativeElement, 'background-color', 'hsl(186, 34%, 60%)');
        }
      });
    }, 1000);

  }

}
